real_input = """

"""

sample_input = """
mjqjpqmgbljsphdztnvjfqwrcgsmlb
"""

prod = True
if prod:
  input = real_input
else:
  input = sample_input

star1 = 0
star2 = 0

input = input.strip('\n')

pos = 0
while pos <= len(input)-4:
  s = {item for item in input[pos:pos+4]}
  if len(s) == 4:
    star1 = pos + 4
    break
  pos += 1

pos = 0
while pos <= len(input)-4:
  s = {item for item in input[pos:pos+14]}
  if len(s) == 14:
    star2 = pos + 14
    break
  pos += 1

print("star 1: ", star1)
print("star 2: ", star2)